import React, { useState, useEffect } from 'react';
import axios from 'axios';

function App() {
  const [properties, setProperties] = useState([]);
  const [form, setForm] = useState({ title: '', price: '', location: '' });
  const [error, setError] = useState(null);

  // Fetch properties on load (with safe error handling)
  useEffect(() => {
    const fetchProperties = async () => {
      try {
        const res = await axios.get('http://localhost:5000/api/properties');
        setProperties(res.data);
      } catch (err) {
        console.error("Error fetching properties:", err.message);
        setError("Could not load properties. Is the backend running?");
      }
    };
    fetchProperties();
  }, []);

  // Handle form input
  const handleChange = (e) => {
    setForm({ ...form, [e.target.name]: e.target.value });
  };

  // Add new property
  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const res = await axios.post('http://localhost:5000/api/properties', form);
      setProperties([...properties, res.data]);
      setForm({ title: '', price: '', location: '' });
    } catch (err) {
      console.error("Error adding property:", err.message);
      setError("Could not add property. Check backend connection.");
    }
  };

  return (
    <div style={{ padding: '2rem', fontFamily: 'Arial, sans-serif' }}>
      <h1>üè° RealEstateAI Dashboard</h1>

      {/* Error message */}
      {error && <p style={{ color: 'red' }}>{error}</p>}

      {/* Form */}
      <form onSubmit={handleSubmit} style={{ marginBottom: '2rem' }}>
        <input
          type="text"
          name="title"
          placeholder="Title"
          value={form.title}
          onChange={handleChange}
          style={{ marginRight: '1rem' }}
        />
        <input
          type="number"
          name="price"
          placeholder="Price"
          value={form.price}
          onChange={handleChange}
          style={{ marginRight: '1rem' }}
        />
        <input
          type="text"
          name="location"
          placeholder="Location"
          value={form.location}
          onChange={handleChange}
          style={{ marginRight: '1rem' }}
        />
        <button type="submit">Add Property</button>
      </form>

      {/* Property list */}
      <ul>
        {properties.map((p) => (
          <li key={p._id}>
            <strong>{p.title}</strong> ‚Äî {p.location} ‚Äî ${p.price}
          </li>
        ))}
      </ul>
    </div>
  );
}

export default App;
