import React, { useState, useEffect } from 'react';
import axios from 'axios';

function App() {
  const [properties, setProperties] = useState([]);
  const [form, setForm] = useState({ title: '', price: '', location: '' });

  // Fetch properties on load
  useEffect(() => {
    axios.get('http://localhost:5000/api/properties')
      .then(res => setProperties(res.data))
      .catch(err => console.error(err));
  }, []);

  // Handle form input
  const handleChange = (e) => {
    setForm({ ...form, [e.target.name]: e.target.value });
  };

  // Add new property
  const handleSubmit = (e) => {
    e.preventDefault();
    axios.post('http://localhost:5000/api/properties', form)
      .then(res => {
        setProperties([...properties, res.data]);
        setForm({ title: '', price: '', location: '' });
      })
      .catch(err => console.error(err));
  };

  return (
    <div style={{ padding: '2rem' }}>
      <h1>üè° RealEstateAI Dashboard</h1>

      {/* Form */}
      <form onSubmit={handleSubmit} style={{ marginBottom: '2rem' }}>
        <input
          type="text"
          name="title"
          placeholder="Title"
          value={form.title}
          onChange={handleChange}
        />
        <input
          type="number"
          name="price"
          placeholder="Price"
          value={form.price}
          onChange={handleChange}
        />
        <input
          type="text"
          name="location"
          placeholder="Location"
          value={form.location}
          onChange={handleChange}
        />
        <button type="submit">Add Property</button>
      </form>

      {/* Property list */}
      <ul>
        {properties.map((p) => (
          <li key={p._id}>
            <strong>{p.title}</strong> ‚Äî {p.location} ‚Äî ${p.price}
          </li>
        ))}
      </ul>
    </div>
  );
}

export default App;
